public class  PizzaDetails {
	String name;
	int price;
	
	public PizzaDetails(String name, int price) {
		// TODO Auto-generated constructor stub
		this.name=name;
		this.price=price;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getPrice() {
		return price;
	}
	public void setPrice(int price) {
		this.price = price;
	}
}
	
	